import React,{Component} from 'react'
import {connect} from 'react-redux'
import {
    AuthorWrap,
    AuthorTop,
    AuthorBottom,
    AuthorItem
} from '../style'
class Author extends Component {
    getAuthor(){
       const {authorMAP} = this.props
       const {author} = authorMAP.toJS()
       const authorUI = []
      if(author){
        author.map(item=>{
            authorUI.push(
             <div></div
             >
            )
       })
      }
      console.log('authorJS', author)
      return author
    }
    render(){
        return (
            <AuthorWrap>
                <AuthorTop>
                    <span>推荐作者</span>
                    <button className="button"><i className="iconfont">&#xe626;</i> 换一批</button>
                </AuthorTop>
                {this.getAuthor()}
            </AuthorWrap>
        )
    }
}
const mapState = state =>({
    authorMAP: state.home.get('bannerList')
})
export default connect(mapState, null)(Author)
