import React, {Component} from 'react'
import {connect} from 'react-redux'
import ArticleUI from './components/article'
import Board from './components/board'
import Author from './components/author'
import {
    HomeWrap,
    HomeBanner,
    HomeLeft,
    HomeRight,
    HomeListUL,
    DownLoad,
    DownLoadInfo
} from './style'
import {actionCreators} from './store'
import AwesomeSwiper from 'react-awesome-swiper';
const config = {
    loop : true,
    autoplay: {
      delay: 3000,
      stopOnLastSlide: false,
      disableOnInteraction: true,
    },
    // Disable preloading of all images
    preloadImages: false,
    // Enable lazy loading
    lazy: true,
    speed: 500,
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
    },
    pagination: {
      el: '.swiper-pagination',
      bulletElement : 'li',
      hideOnClick :true,
      clickable :true,
    },
    on: {
      slideChange: function () {
        console.log(this.activeIndex);
      },
    },
  };
class Home extends Component{
    swiperRef = null
    bannerRender(){
        const {bannerList} = this.props
        const bannerListJS = bannerList.toJS()
        const bannerUI = []
        if(bannerListJS.banner){
              bannerListJS.banner.map(item =>{
                  bannerUI.push(
                    <img className='img' src={item.imgUrl} alt="" key={item.id}/>
                  )
              })
            return bannerUI
         }
    }
    render(){
        return (
            <HomeWrap>
                <HomeLeft>
                    <HomeBanner>
                        {
                            this.bannerRender()
                        }
                    </HomeBanner>
                    <HomeListUL>
                       <ArticleUI />
                    </HomeListUL>
                </HomeLeft>
                <HomeRight>
                    <Board />
                    <DownLoad >
                        <img src="./images/erweima.png" alt="" />
                        <DownLoadInfo> 
                           <div>
                                <p>下载简书手机App<i className="iconfont">＞ </i></p>
                           </div>
                           <p className="description">随时随地发现和创作内容</p>
                        </DownLoadInfo>
                    </DownLoad>
                    <Author />
                </HomeRight>
            </HomeWrap>
        )
    }
    componentDidMount(){
        this.props.getBanner()
    }
}
const mapStateToProps = (state)=>{
    return {
        bannerList: state.home.get('bannerList')
    }
}
const mapDispathToProps = (dispatch)=>{
    return {
        getBanner(){
            dispatch(actionCreators.getBannerList())
        }
    }
}
export default connect(mapStateToProps, mapDispathToProps)(Home)
