import React,{Component} from 'react'
import {connect} from 'react-redux'
import {
    BoardWrap,
    BoarList
} from '../style'
class Board extends Component{
    getBoard(){
        const {boarList} = this.props
        const {boardJS} = boarList.toJS()
        const boardUI = []
        if(boardJS){
            boardJS.map(item =>(
                <BoarList imgUrl='/images/3.png' key={item.id}></BoarList>
            ))
        }
        console.log('Board', boardJS)
        // <BoarList imgUrl='/images/3.png'></BoarList>
        return boardJS
    }
    render(){
        return (
            <BoardWrap>
                {this.getBoard()}
            </BoardWrap>
        )
    }
}
const mapState = state =>({
    boarList: state.home.get('bannerList')
})
const mapDispatch = dispatch =>({
})
export default connect(mapState, mapDispatch)(Board)
