import React,{Component} from 'react'
import {connect} from 'react-redux'
import {
    AuthorWrap,
    AuthorTop,
    AuthorBottom,
    AuthorItem
} from '../style'
class Author extends Component {
    co
    getAuthor(){
       const {authorMAP} = this.props
       const {author} = authorMAP.toJS()
       const authorUI = []
      if(author){
        let authorTotalPage = Math.ceil(author.length / 4)
        let authorPage = 1
          console.log('authorauthor', author)
          for (let i = (authorPage - 1) * 4; i < authorPage *4; i++){

          }
        author.map(item=>{
            authorUI.push(
                <AuthorItem  key={item.id}>
                    <a className="avatar" href="/u/631131ed01af?utm_source=desktop&amp;utm_medium=index-users" target="_blank" className="avatar">
                        <img src= {item.avatar} alt="" />
                    </a>
                    <a className="follow" state="0" href="" target="_blank"><i className="iconfont ic-follow"></i>关注</a>
                    <a href="/u/631131ed01af?utm_source=desktop&amp;utm_medium=index-users" target="_blank" className="name">
                        {item.author}
                    </a>
                    <p>{item.content}</p>
                </AuthorItem>
            )
       })
      }
      return authorUI
    }
    render(){
        return (
            <AuthorWrap>
                <AuthorTop>
                    <span>推荐作者</span>
                    <button className="button" onClick={()=>this.zhuanClick(this.zhuan)}><i className="iconfont rote" ref={(icon)=> this.zhuan = icon}>&#xe626;</i> 换一批</button>
                </AuthorTop>
                <AuthorBottom>
                    {this.getAuthor()}
                </AuthorBottom>
            </AuthorWrap>
        )
    }
    zhuanClick(el){
        let rotate = el.style.transform.replace(/[^0-9]/ig, '')
        if(rotate){
            rotate = parseInt(rotate, 10)
        }else{
            rotate = 0
        }
        el.style.transform = `rotate(${rotate + 360}deg)`
        console.log('el', rotate)
    }
}
const mapState = state =>({
    authorMAP: state.home.get('bannerList')
})
export default connect(mapState, null)(Author)
