                       <ArticleUI />
                       <MoreLIst onClick={()=> this.props.moreClick()}>加载更多</MoreLIst>
                    </HomeListUL>
                </HomeLeft>
                <HomeRight>
                    <Board />
                    <DownLoad >
                        <img src="./images/erweima.png" alt="" />
