import React,{Component,Fragment} from 'react'
import {connect} from 'react-redux'
import {
    AuthorWrap,
    AuthorTop,
    AuthorBottom,
    AuthorItem
} from '../style'
class Author extends Component {
    getAuthor(){
       const {authorMAP} = this.props
       const {author} = authorMAP.toJS()
       const authorUI = []
       if(author){
        author.map(item=>{
            authorUI.push(
                    <Fragment key={item.id}>
                        <AuthorTop>
                            <span>推荐作者</span>
                            <button className="button"><i className="iconfont">&#xe626;</i> 换一批</button>
                        </AuthorTop>
                        <AuthorBottom>
                            <AuthorItem>
                                <a className="avatar" href="/u/631131ed01af?utm_source=desktop&amp;utm_medium=index-users" target="_blank" className="avatar">
                                    <img src="" alt="" />
                                </a>
                                <a className="follow" state="0" href="" target="_blank"><i className="iconfont ic-follow"></i>关注</a>
                                <a href="/u/631131ed01af?utm_source=desktop&amp;utm_medium=index-users" target="_blank" className="name">
                                    {item.author}
                                </a>
                                <p> {item.content}</p>
                            </AuthorItem>
                        </AuthorBottom>
                    </Fragment>
                )
        })
       }
     
       console.log('authorJS', author)
       return authorUI
    }
    render(){
        return (
            <AuthorWrap>
                {this.getAuthor()}
               
            </AuthorWrap>
        )
    }
}
const mapState = state =>({
    authorMAP: state.home.get('bannerList')
})
export default connect(mapState, null)(Author)
