import React,{Component} from 'react'
import {connect} from 'react-redux'
import {
    BoardWrap,
    BoarList
} from '../style'
class Board extends Component{
    getBoard(){
        const {boarList} = this.props
        const {board} = boarList.toJS()
        console.log('boardJS', boarList.toJS())
        const boardUI = []
        if(board){
            board.map(item =>{
                boardUI.push(
                    <BoarList imgUrl= '%PUBLIC_URL%/images/1.jpg' key={item.id}></BoarList>
                )
            })
        }
        console.log('Board', boardUI)
        // <BoarList imgUrl='/images/3.png'></BoarList>
        return boardUI
    }
    render(){
        return (
            <BoardWrap>
                <BoarList imgUrl= './images/erweima.png' ></BoarList>
                <BoarList imgUrl={process.env.PUBLIC_URL + '/images/2.png'} ></BoarList>
                <BoarList imgUrl={process.env.PUBLIC_URL + '/images/0.png'} ></BoarList>
            </BoardWrap>
        )
    }
}
const mapState = state =>({
    boarList: state.home.get('bannerList')
})
const mapDispatch = dispatch =>({
})
export default connect(mapState, mapDispatch)(Board)
