import React, {Component} from 'react'
import {CSSTransition} from 'react-transition-group'
import {connect} from 'react-redux'
import {
    HeaderWrapper,
    Logo,
    Nav,
    NavItem,
    NavSearch,
    NavSearchWrap,
    Addition,
    Button
} from './style'
class Header extends Component {
    constructor(props){
        super(props)
        this.state = {
            foucsd: false
        }
        this.searchFocus = this.searchFocus.bind(this)
        this.searchBlur = this.searchBlur.bind(this)
    }
    render(){
        return (
            <HeaderWrapper>
                <Logo></Logo>
                <Nav>
                    <NavItem className='left active'>首页</NavItem>
                    <NavItem className='left'>下载app</NavItem>
                    <NavItem className='right'>登陆</NavItem>
                    <NavItem className='right'>
                      <span className="iconfont Aa">&#xe636;</span>
                    </NavItem>
                    <NavSearchWrap>
                        <CSSTransition 
                            in={this.state.foucsd}
                            timeout={200}
                            classNames='slide'
                        >
                            <NavSearch className={this.state.foucsd ? 'foucsd': ''} 
                                        onFocus={this.searchFocus}
                                        onBlur={this.searchBlur}
                            ></NavSearch>
                        </CSSTransition>
                      <i className={this.state.foucsd ? 'foucsds iconfont': 'iconfont'}>&#xe782;</i>
                    </NavSearchWrap>
                    <Addition>
                        <Button className='write'>
                         <i className='iconfont yumao'>&#xe600;</i>
                          写文章
                        </Button>
                        <Button className='reg'>注册</Button>
                    </Addition>
                </Nav>
            </HeaderWrapper>
        )
    }
    searchFocus(){
        this.setState({
            foucsd: true
        })
    }
    searchBlur(){
        this.setState({
            foucsd: false
        })
    }
}
const mapStateToProps = ()=>{
    return {
        
    }
}
const mapDispathToProps = ()=>{
    
}
export default connect(mapStateToProps, mapDispathToProps)(Header)
