const defaultState = {
    foucsd: false
}
export default (state = defaultState, action) =>{
    if(action.type === 'searchFocus'){
        state.foucsd = action.data
        return {
            foucsd: true
        }
    }
    if(action.type === 'searchBlur'){
        state.foucsd = action.data
        return state
    }
    return state
}
