import React, {Fragment, Component} from 'react'
import { connect } from 'react-redux'
import {
    HomeListLI,
    HomeTitle,
    Content
} from '../style'

class ArticleUI extends Component {
    getArticle(){
     const {home} =  this.props
     const {article} = home.toJS() 
     const articelArrui = []
    if(article){
        article.map(item => {
            articelArrui.push(
                <HomeListLI key={item.id}>
                <div>
            <HomeTitle>{item.title}</HomeTitle>
                    <Content>
                       {item.content}
                    </Content>
                    <div>
                        <span className="jsd-meta">
                            <i className="iconfont">&#xe619;</i>
                            
                        </span>
                        <span className="nickname">{item.author}</span>
                        <span className="message">
                            <i className="iconfont">&#xe667;</i>
                            {item.message}
                        </span>
                        <span className="xin">
                            <i className="iconfont">&#xe755;</i>
                        {item.collect}
                        </span>
                    </div>
                </div>
                <div>
                    <img src='./images/1.jpg' alt=""/>
                </div>
            </HomeListLI>
            )
        })
    }
    console.log('bannerList', article)
    return articelArrui
    }
   render(){
    return (
        <Fragment>
            {
                this.getArticle()
            }
        </Fragment>
    )
   }
}
const mapStateToProps = state =>({
    home: state.home.get('bannerList')
})
const mapDispachToProps = dispatch =>({

})
export default connect(mapStateToProps, mapDispachToProps)(ArticleUI)
