import React, {Component} from 'react'
import {CSSTransition} from 'react-transition-group'
import {connect} from 'react-redux'
import {actionCreators} from './store' 
import {
    HeaderWrapper,
    Logo,
    Nav,
    NavItem,
    NavSearch,
    NavSearchWrap,
    Addition,
    Button,
    SearchInfo,
    SearchItem,
    SearchTop
} from './style'
class Header extends Component {
    showSearch(){
        const {list,page, totalPage, foucsd, mouseIn, handlenMouseLeave,handlenMouseEnter, handlenToggle} = this.props
        const newList = list.toJS()
        const pageList = []
        if(newList.length > 0){
            //数据 15条 cei 15 / 10 = 2(totalpage)
            for (let i = (page - 1) * 10; i < page * 10; i++) {
                if(newList[i]){
                    pageList.push(
                        <SearchItem key={newList[i]}>{newList[i]}</SearchItem>
                    )
                }
             }
        }
        if(foucsd || mouseIn){
            return (
                <SearchInfo onMouseEnter={handlenMouseEnter} onMouseLeave={handlenMouseLeave}>
                        <SearchTop>
                            <span>热门搜索</span>
                            <a  onClick= {() => handlenToggle(page, totalPage)}>换一批</a>
                        </SearchTop>
                        <div>
                            {pageList}
                        </div>
                </SearchInfo>
            )
        }else{
            return null
        }
       
    }
    render(){
        return (
            <HeaderWrapper>
                <Logo></Logo>
                <Nav>
                    <NavItem className='left active'>首页</NavItem>
                    <NavItem className='left'>下载app</NavItem>
                    <NavItem className='right'>登陆</NavItem>
                    <NavItem className='right'>
                      <span className="iconfont Aa">&#xe636;</span>
                    </NavItem>
                    <NavSearchWrap>
                        <CSSTransition 
                            in={this.props.foucsd}
                            timeout={200}
                            classNames='slide'
                        >
                            <NavSearch className={this.props.foucsd ? 'foucsd': ''} 
                                        onFocus={this.props.searchFocus}
                                        onBlur={this.props.searchBlur}
                            ></NavSearch>
                        </CSSTransition>
                      <i className={this.props.foucsd ? 'foucsds iconfont': 'iconfont'}>&#xe782;</i>
                      {this.showSearch()}
                    </NavSearchWrap>
                    <Addition>
                        <Button className='write'>
                         <i className='iconfont yumao'>&#xe600;</i>
                          写文章
                        </Button>
                        <Button className='reg'>注册</Button>
                    </Addition>
                </Nav>
            </HeaderWrapper>
        )
    }
}
const mapStateToProps = (state)=>{
    return {
        foucsd: state.header.get('foucsd'),
        list: state.header.get('list'),
        mouseIn: state.header.get('mouseIn'),
        page: state.header.get('page'),
        totalPage: state.header.get('totalPage')
    }
}
const mapDispathToProps = (dispatch)=>{
    return {
        searchFocus(){
            dispatch(actionCreators.getList())
            dispatch(actionCreators.searchFocus())
        },
        searchBlur(){
            dispatch(actionCreators.searchBlur())
        },
        handlenMouseEnter(){
            dispatch(actionCreators.mouseEnter())
        },
        handlenMouseLeave(){
            dispatch(actionCreators.mouseLeave())
        },
        handlenToggle(page, totalPage){
            if(page < totalPage){
                dispatch(actionCreators.changePage(page +1))
            }else{
                dispatch(actionCreators.changePage(1))
            }
        }
    }
}
export default connect(mapStateToProps, mapDispathToProps)(Header)
