import React, {Fragment, Component} from 'react'
import { connect } from 'react-redux'
import {
    HomeListLI,
    HomeTitle,
    Content
} from '../style'

class ArticleUI extends Component {
    getArticle(){
     const {home} =  this.props
     const {article} = home.toJS() 
     const articelArrui = []
    if(article){
        article.map(item => {
            articelArrui.push(
                <HomeListLI key={item}>
                <div>
                    <HomeTitle>快节奏，慢生活!</HomeTitle>
                    <Content>
                        看到5年前一个周末的文字，突然发现现在字怎么写的还不如从前! 快节奏慢， 今天在家体验了一次慢生活。
                        以往每到周末都会和朋友约上去登山去远足去锻...
                    </Content>
                    <div>
                        <span className="jsd-meta">
                            <i className="iconfont">&#xe619;</i>
                            2.4
                        </span>
                        <span className="nickname">一点思考</span>
                        <span className="message">
                            <i className="iconfont">&#xe667;</i>
                            9
                        </span>
                        <span className="xin">
                            <i className="iconfont">&#xe755;</i>
                        36
                        </span>
                    </div>
                </div>
                <div>
                    <img src='./images/1.jpg' alt=""/>
                </div>
            </HomeListLI>
            )
        })
    }
    console.log('bannerList', article)
    return articelArrui
    }
   render(){
    return (
        <Fragment>
            {
                this.getArticle()
            }
        </Fragment>
    )
   }
}
const mapStateToProps = state =>({
    home: state.home.get('bannerList')
})
const mapDispachToProps = dispatch =>({

})
export default connect(mapStateToProps, mapDispachToProps)(ArticleUI)
