    preloadImages: false,
    // Enable lazy loading
    lazy: true,
    speed: 500,
    navigation: {
      nextEl: '.swiper-button-next',
      prevEl: '.swiper-button-prev',
    },
    pagination: {
      el: '.swiper-pagination',
      bulletElement : 'li',
      hideOnClick :true,
      clickable :true,
    },
    on: {
      slideChange: function () {
        console.log(this.activeIndex);
      },
    },
  };
  swiperRef = null
  goNext = () => {//use `this.swiperRef.swiper` to get the instance of Swiper
      swiperRef.swiper.slideNext();
       
  }
  
class  ReactBanner extends Component{
   render () {
       return (
        <HomeBanner>
            <AwesomeSwiper ref={ref => (this.swiperRef = ref)} config={config} className="your-classname">
