import React, {Component} from 'react'
import {connect} from 'react-redux'
import ArticleUI from './components/article'
import Board from './components/board'
import Author from './components/author'
import {
    HomeWrap,
    HomeBanner,
    HomeLeft,
    HomeRight,
    HomeListUL,
    DownLoad,
    DownLoadInfo
} from './style'
import {actionCreators} from './store'
import AwesomeSwiper from 'react-awesome-swiper';

class Home extends Component{
    swiperRef = null
    goNext = () => {//use `this.swiperRef.swiper` to get the instance of Swiper
         this.swiperRef.swiper.slideNext();
         
    }
    bannerRender(){
        const {bannerList} = this.props
        const bannerListJS = bannerList.toJS()
        const bannerUI = []
        if(bannerListJS.banner){
              bannerListJS.banner.map(item =>{
                  bannerUI.push(
                    <div className="swiper-slide" key={item.id}>
                        <img className='img' src={item.imgUrl} alt="" />
                    </div>
                  )
              })
            return bannerUI
         }
    }
    render(){
        return (
            <HomeWrap>
                <HomeLeft>
                    
                    <HomeListUL>
                       <ArticleUI />
                    </HomeListUL>
                </HomeLeft>
                <HomeRight>
                    <Board />
                    <DownLoad >
                        <img src="./images/erweima.png" alt="" />
                        <DownLoadInfo> 
                           <div>
                                <p>下载简书手机App<i className="iconfont">＞ </i></p>
                           </div>
                           <p className="description">随时随地发现和创作内容</p>
                        </DownLoadInfo>
                    </DownLoad>
                    <Author />
                </HomeRight>
            </HomeWrap>
        )
    }
    componentDidMount(){
        this.props.getBanner()
    }
}
const mapStateToProps = (state)=>{
    return {
        bannerList: state.home.get('bannerList')
    }
}
const mapDispathToProps = (dispatch)=>{
    return {
        getBanner(){
            dispatch(actionCreators.getBannerList())
        }
    }
}
export default connect(mapStateToProps, mapDispathToProps)(Home)
